import React from 'react'
import Particles from 'react-particles-js'

export default class Layout extends React.Component{
  componentWillReceiveProps() {
    const particleOpt = {
      particles: {
        number: {
          value: 50,
          density: {
            enable: false,
            value_area: 500
          },
          move: {
            enable: true,
            speed: 1
          }
        }
      },
      interactivity: {
        events: {
            onhover: {
                enable: true,
                mode: ["push"]
            }
        }
      }
    }
  }
  render(){
    return(
      <div>
        <Particles canvasClassName="particle-canvas" params={particleOpt} />
        {props.children}
      </div>
    )
  }
}
